import React from 'react'

class Square extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            revealed: this.props.revealed
            //display hidden-- once clicked -> true
        }
    }

    handleClick = (e) => {
        if (this.props.data === 'b') {
            alert("YOU LOSE")
        } else if (this.props.data === 0) {
            this.props.handleSquareClick(e, this.props.coords)
        }
        let revealed = true
        this.setState({ revealed })
    }

    render() {
      if (this.state.revealed) {
        console.log();

      }
        let cssClick = this.state.revealed ? 'clickedsquare' : 'unclickedsquare'
        return (
            <td >
                <div className="square" onClick={this.handleClick}>
                    <div className={cssClick}>
                        {this.props.data}
                    </div>
                </div>
            </td >
        )
    }
}

export default Square;
