import React from 'react'
import GameMenu from '../containers/GameMenu'

class NewGameMenu extends React.Component {
    constructor(props) {
        super(props)

        this.handleChoice = this.handleChoice.bind(this)
    }

    handleChoice(event) {
        const difficulty = event.target.innerText.toLowerCase();
        this.props.handleDifficultyClick(difficulty)
    }

    render() {
        return (
            <div className='menu'>
              <button onClick={this.props.restart}>
                  RESTART
              </button>
              <GameMenu />
            </div>
        )
    }
}

export default NewGameMenu;
